import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./App.css";

const App = () => {
  // Define the color buttons/capabilities
  const [colorButtons] = useState([
    { id: "green", color: "#4CAF50", label: "Green" },
    { id: "red", color: "#f44336", label: "Red" },
    { id: "yellow", color: "#FFEB3B", label: "Yellow" },
    { id: "black", color: "#333333", label: "Black" },
    { id: "orange", color: "#FF9800", label: "Orange" },
    { id: "blue", color: "#2196F3", label: "Blue" },
  ]);

  // Create initial grid state (30 rows, 12 weeks)
  const createInitialGrid = () => {
    const grid = {};
    for (let row = 1; row <= 30; row++) {
      for (let week = 1; week <= 12; week++) {
        grid[`${row}-${week}`] = null;
      }
    }
    return grid;
  };

  const [gridData, setGridData] = useState(createInitialGrid());

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) {
      return;
    }

    // If dropping on the grid
    if (destination.droppableId.startsWith("cell-")) {
      const cellId = destination.droppableId.replace("cell-", "");
      const colorButton = colorButtons.find(
        (btn) => btn.id === draggableId || btn.id === draggableId.split("-")[0]
      );

      if (colorButton) {
        setGridData((prevGrid) => ({
          ...prevGrid,
          [cellId]: { ...colorButton, uniqueId: Date.now().toString() },
        }));
      }
    }

    // If moving within the grid
    if (
      source.droppableId.startsWith("cell-") &&
      destination.droppableId.startsWith("cell-")
    ) {
      const sourceCellId = source.droppableId.replace("cell-", "");
      const destCellId = destination.droppableId.replace("cell-", "");

      const sourceItem = gridData[sourceCellId];

      setGridData((prevGrid) => ({
        ...prevGrid,
        [sourceCellId]: null,
        [destCellId]: sourceItem,
      }));
    }

    // If removing from grid (dragging back to sidebar or outside)
    if (
      source.droppableId.startsWith("cell-") &&
      !destination.droppableId.startsWith("cell-")
    ) {
      const sourceCellId = source.droppableId.replace("cell-", "");
      setGridData((prevGrid) => ({
        ...prevGrid,
        [sourceCellId]: null,
      }));
    }
  };

  const generateWeekHeaders = () => {
    const weeks = [];
    for (let week = 1; week <= 12; week++) {
      weeks.push(
        <th key={week} className="week-header">
          Week {week}
        </th>
      );
    }
    return weeks;
  };

  const generateGridRows = () => {
    const rows = [];
    for (let row = 1; row <= 30; row++) {
      const cells = [];

      // Row number cell
      cells.push(
        <td key={`row-${row}`} className="row-number">
          {row}
        </td>
      );

      // Week cells
      for (let week = 1; week <= 12; week++) {
        const cellId = `${row}-${week}`;
        const item = gridData[cellId];

        cells.push(
          <td key={cellId} className="grid-cell">
            <Droppable droppableId={`cell-${cellId}`} type="CAPABILITY">
              {(provided, snapshot) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className={`cell-content ${
                    snapshot.isDraggingOver ? "dragging-over" : ""
                  }`}
                >
                  {item && (
                    <Draggable
                      draggableId={`${item.id}-${item.uniqueId}`}
                      index={0}
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className={`color-dot ${
                            snapshot.isDragging ? "dragging" : ""
                          }`}
                          style={{
                            backgroundColor: item.color,
                            ...provided.draggableProps.style,
                          }}
                        />
                      )}
                    </Draggable>
                  )}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </td>
        );
      }

      rows.push(<tr key={row}>{cells}</tr>);
    }
    return rows;
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="app">
        <header className="app-header">
          <h1>3 Month Capability Matrix</h1>
        </header>

        <div className="main-content">
          <div className="sidebar">
            <h3>Capabilities</h3>
            <Droppable droppableId="sidebar" type="CAPABILITY">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="color-buttons"
                >
                  {colorButtons.map((button, index) => (
                    <Draggable
                      key={button.id}
                      draggableId={button.id}
                      index={index}
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className={`color-button ${
                            snapshot.isDragging ? "dragging" : ""
                          }`}
                          style={{
                            backgroundColor: button.color,
                            ...provided.draggableProps.style,
                          }}
                        >
                          {button.label}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>

            <div className="instructions">
              <h4>Instructions:</h4>
              <ul>
                <li>Drag color buttons to grid cells</li>
                <li>Move items between cells</li>
                <li>Drag items back to remove them</li>
              </ul>
            </div>
          </div>

          <div className="matrix-container">
            <table className="capability-matrix">
              <thead>
                <tr>
                  <th className="row-header">No.</th>
                  {generateWeekHeaders()}
                </tr>
              </thead>
              <tbody>{generateGridRows()}</tbody>
            </table>
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default App;
